"use strict";(self.webpackChunkmisinfocon_india_docs=self.webpackChunkmisinfocon_india_docs||[]).push([[6439],{9105:i=>{i.exports=JSON.parse('{"label":"information gap","permalink":"/misinfocon-india-docs/docs/tags/information-gap","allTagsPath":"/misinfocon-india-docs/docs/tags","count":1,"items":[{"id":"proposals/addressing-missing-and-mis-information","title":"Addressing Missing and Mis-Information","description":"Through this project, we aim to address the missing information and misinformation problems about Intersex and Asexual communities across Karnataka. This project is not limited to building an information system for communities, but ensuring the same is available and accessible in regional languages. The corpus generated, would be made public under a creative commons license. We aim to leverage free and open source, audio/video narration, annotation, and story building platforms.","permalink":"/misinfocon-india-docs/docs/proposals/addressing-missing-and-mis-information"}]}')}}]);